conn = sqlite3.connect('session_schedule.db')
c = conn.cursor()
c.execute('''CREATE TABLE IF NOT EXISTS schedule (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    group_name TEXT,
    date TEXT,
    time TEXT,
    subject TEXT,
    type TEXT,
    room TEXT,
    teacher TEXT
)''')

c.execute('DELETE FROM schedule WHERE group_name = "241-325"')
for event in schedule:
    c.execute('''INSERT INTO schedule (group_name, date, time, subject, type, room, teacher)
                 VALUES (?, ?, ?, ?, ?, ?, ?)''',
              (event['group_name'], event['date'], event['time'], event['subject'], event['type'], event['room'], event['teacher']))
conn.commit()
conn.close()
